mapper.select:
SELECT RESERVATION_TIME
  FROM TD_RESERVATION
 WHERE CLINIC_INSTINUM = #{clinicInstinum}
   AND RESERVATION_DATE = #{reservationDate}

mapper.update:
UPDATE TD_RESERVATION
   SET RESERVATION_STATUS = '예약확정'
 WHERE RESERVATION_SEQ = #{value}

UPDATE TD_RESERVATION
   SET RESERVATION_STATUS = '예약취소'
 WHERE RESERVATION_SEQ = #{value}
